import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { clearAuth, getRole } from '../auth';
import { 
  FaMoon, 
  FaSun, 
  FaPlus, 
  FaUser, 
  FaList, 
  FaSignInAlt, 
  FaUserPlus, 
  FaSignOutAlt, 
  FaLeaf,
  FaBars,
  FaEnvelope
} from 'react-icons/fa';

export default function Navbar() {
  const navigate = useNavigate();
  const [role, setRole] = useState(null);
  const [isDark, setIsDark] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    setRole(getRole());
    // Check initial theme
    const html = document.querySelector("html");
    const currentTheme = html?.getAttribute("data-theme") || "light";
    setIsDark(currentTheme === "dark");
  }, []);

  const toggleTheme = () => {
    const html = document.querySelector("html");
    const current = html?.getAttribute("data-theme") || "light";
    const newTheme = current === "light" ? "dark" : "light";
    html?.setAttribute("data-theme", newTheme);
    setIsDark(newTheme === "dark");
  };

  const logout = () => {
    clearAuth();
    setRole(null);
    navigate("/login");
  };

  return (
    <div className="shadow-lg navbar rounded-b-xl" style={{ backgroundColor: '#555879' }}>
      <div className="flex-1">
        <Link to="/" className="btn btn-ghost text-xl font-bold text-[#F9F3EF] hover:bg-transparent">
          <FaLeaf className="mr-2" />
          Food Rescue
        </Link>
      </div>
      
      <div className="flex-none">
        {/* Mobile menu button */}
        <div className="md:hidden">
          <label htmlFor="mobile-menu" className="text-white btn btn-ghost">
            <FaBars className="w-5 h-5" />
          </label>
        </div>

        {/* Desktop Navigation */}
        <ul className="hidden p-0 menu menu-horizontal md:flex">
          {/* Listings Link */}
          <li>
            <Link 
              to="/listings" 
              className="flex items-center text-[#F9F3EF] hover:bg-[#98A1BC] hover:text-[#555879] rounded-lg transition-colors duration-200"
            >
              <FaList className="mr-2" />
              Listings
            </Link>
          </li>

          {/* Create Listing Link (Donors only) */}
          {role === 'donor' && (
            <li>
              <Link 
                to="/create-listing" 
                className="flex items-center text-[#F9F3EF] hover:bg-[#98A1BC] hover:text-[#555879] rounded-lg transition-colors duration-200"
              >
                <FaPlus className="mr-2" />
                Create Listing
              </Link>
            </li>
          )}

          {/* Profile Link */}
          {role && (
            <li>
              <Link 
                to="/profile" 
                className="flex items-center text-[#F9F3EF] hover:bg-[#98A1BC] hover:text-[#555879] rounded-lg transition-colors duration-200"
              >
                <FaUser className="mr-2" />
                Profile
              </Link>
            </li>
          )}

          {/* Theme Toggle Button */}
          <li>
            <button 
              className="flex items-center btn btn-ghost text-[#F9F3EF] hover:bg-[#98A1BC] hover:text-[#555879] rounded-full transition-colors duration-200" 
              onClick={toggleTheme}
              aria-label="Toggle theme"
            >
              {isDark ? <FaSun className="w-4 h-4" /> : <FaMoon className="w-4 h-4" />}
            </button>
          </li>

          {/* Auth Buttons */}
          {!role ? (
            <>
              <li>
                <Link 
                  to="/login" 
                  className="flex items-center btn btn-outline border-[#F9F3EF] text-[#F9F3EF] hover:bg-[#F9F3EF] hover:text-[#555879] rounded-lg transition-colors duration-200"
                >
                  <FaSignInAlt className="mr-2" />
                  Login
                </Link>
              </li>
              <li>
                <Link 
                  to="/register" 
                  className="flex items-center btn btn-primary bg-[#F9F3EF] text-[#555879] hover:bg-[#98A1BC] hover:text-white border-none rounded-lg transition-colors duration-200"
                >
                  <FaUserPlus className="mr-2" />
                  Register
                </Link>
              </li>
            </>
          ) : (
            <li>
              <button 
                className="flex items-center btn btn-outline border-[#F9F3EF] text-[#F9F3EF] hover:bg-[#F9F3EF] hover:text-[#555879] rounded-lg transition-colors duration-200" 
                onClick={logout}
              >
                <FaSignOutAlt className="mr-2" />
                Logout
              </button>
            </li>
          )}
        </ul>
      </div>

      {/* Mobile Menu Drawer */}
      <div className="drawer-side md:hidden">
        <label htmlFor="mobile-menu" className="drawer-overlay"></label>
        <ul className="min-h-full p-4 menu w-80 bg-base-200 text-base-content">
          {/* Close button */}
          <li className="mb-4">
            <label htmlFor="mobile-menu" className="absolute btn btn-ghost btn-sm btn-circle right-2 top-2">
              ✕
            </label>
          </li>

          {/* Mobile menu items */}
          <li>
            <Link 
              to="/listings" 
              className="flex items-center"
              onClick={() => setIsMobileMenuOpen(false)}
            >
              <FaList className="mr-3" />
              Listings
            </Link>
          </li>

          {role === 'donor' && (
            <li>
              <Link 
                to="/create-listing" 
                className="flex items-center"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                <FaPlus className="mr-3" />
                Create Listing
              </Link>
            </li>
          )}

          {role && (
            <li>
              <Link 
                to="/profile" 
                className="flex items-center"
                onClick={() => setIsMobileMenuOpen(false)}
              >
                <FaUser className="mr-3" />
                Profile
              </Link>
            </li>
          )}

          <li>
            <button 
              className="flex items-center" 
              onClick={toggleTheme}
            >
              {isDark ? <FaSun className="mr-3" /> : <FaMoon className="mr-3" />}
              {isDark ? 'Light Mode' : 'Dark Mode'}
            </button>
          </li>

          {!role ? (
            <>
              <li>
                <Link 
                  to="/login" 
                  className="flex items-center"
                  onClick={() => setIsMobileMenuOpen(false)}
                >
                  <FaSignInAlt className="mr-3" />
                  Login
                </Link>
              </li>
              <li>
                <Link 
                  to="/register" 
                  className="flex items-center"
                  onClick={() => setIsMobileMenuOpen(false)}
                >
                  <FaUserPlus className="mr-3" />
                  Register
                </Link>
              </li>
            </>
          ) : (
            <li>
              <button 
                className="flex items-center" 
                onClick={() => {
                  logout();
                  setIsMobileMenuOpen(false);
                }}
              >
                <FaSignOutAlt className="mr-3" />
                Logout
              </button>
            </li>
          )}
        </ul>
      </div>
    </div>
  );
}
