import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, signInAnonymously } from 'firebase/auth';
import { getFirestore, doc, setDoc, getDoc, onSnapshot } from 'firebase/firestore';

// IMPORTANT: DO NOT change the values of these variables. They are provided by the hosting environment.
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : '';
// Logging for debugging purposes
console.log('Firebase App ID:', appId);
console.log('Firebase Config:', firebaseConfig);

export default function Profile() {
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [userId, setUserId] = useState(null);
  const [userRole, setUserRole] = useState('');
  const [name, setName] = useState('');
  const [city, setCity] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  // Initialize Firebase and authenticate user
  useEffect(() => {
    try {
      if (Object.keys(firebaseConfig).length > 0) {
        const app = initializeApp(firebaseConfig);
        const firestoreDb = getFirestore(app);
        const authService = getAuth(app);
        setDb(firestoreDb);
        setAuth(authService);
        console.log("Firebase initialized successfully.");

        const authenticate = async () => {
          try {
            if (initialAuthToken) {
              await signInWithCustomToken(authService, initialAuthToken);
              console.log("Signed in with custom token.");
            } else {
              await signInAnonymously(authService);
              console.log("Signed in anonymously.");
            }
          } catch (error) {
            console.error("Firebase Auth Error:", error);
            setMessage("Authentication failed. Please try again.");
          }
        };

        const unsubscribe = authService.onAuthStateChanged(user => {
          if (user) {
            setUserId(user.uid);
            console.log("User ID:", user.uid);
            setIsAuthReady(true);
            // This is a placeholder for your existing getRole() logic
            // You will need to implement this based on your app's user management
            setUserRole('donor'); // or 'ngo'
          } else {
            console.log("No user is signed in.");
            setUserId(null);
            setIsAuthReady(true);
          }
          setIsLoading(false);
        });

        authenticate();
        return () => unsubscribe();
      } else {
        console.error("Firebase config is missing.");
        setIsLoading(false);
      }
    } catch (e) {
      console.error("Failed to initialize Firebase:", e);
      setIsLoading(false);
    }
  }, []);

  // Set up real-time listener for user profile data
  useEffect(() => {
    if (!db || !userId || !userRole || !isAuthReady) return;

    const collectionPath = `/artifacts/${appId}/users/${userId}`;
    const docPath = `${collectionPath}/profiles/user-profile`;
    const userProfileRef = doc(db, docPath);

    const unsubscribe = onSnapshot(userProfileRef, (docSnap) => {
      if (docSnap.exists()) {
        const profileData = docSnap.data();
        setName(profileData.name || '');
        setCity(profileData.city || '');
        setEmail(profileData.contact_email || '');
        setMessage(`Profile found and loaded. Your unique ID is ${userId}`);
      } else {
        setMessage(`No profile found. Please create one. Your unique ID is ${userId}`);
      }
    }, (error) => {
      console.error("Firestore Error:", error);
      setMessage("Failed to load profile. Please check your connection.");
    });

    return () => unsubscribe();
  }, [db, userId, userRole, isAuthReady]);

  const saveProfile = async () => {
    if (!db || !userId) {
      setMessage("Database not ready. Please wait.");
      return;
    }

    setMessage('');
    if (!name || !city || !email) {
      setMessage('All fields are required.');
      return;
    }

    const collectionPath = `/artifacts/${appId}/users/${userId}`;
    const docPath = `${collectionPath}/profiles/user-profile`;
    const userProfileRef = doc(db, docPath);

    try {
      await setDoc(userProfileRef, {
        name,
        city,
        contact_email: email,
        role: userRole,
      }, { merge: true });
      setMessage(`Profile saved successfully!`);
    } catch (err) {
      console.error("Firestore Save Error:", err);
      setMessage(`Failed to save profile: ${err.message}`);
    }
  };
  
  const cardStyles = {
    backgroundColor: '#F9F3EF',
    borderRadius: '1rem',
    border: '1px solid #98A1BC'
  };

  const messageBoxStyles = {
    backgroundColor: '#555879'
  };

  const buttonStyles = {
    backgroundColor: '#555879',
    borderColor: '#555879'
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center text-xl font-bold text-[#555879]">
        Loading profile...
      </div>
    );
  }

  return (
    <div className="flex items-center justify-center min-h-screen p-4" style={{ backgroundColor: '#F9F3EF' }}>
      <div className="w-full max-w-md shadow-lg card rounded-xl" style={cardStyles}>
        <div className="card-body">
          <h2 className="card-title text-2xl font-bold text-[#555879] mb-4">
            Manage {userRole?.toUpperCase()} Profile
          </h2>

          {message && (
            <div
              className="p-3 mb-4 font-semibold text-white rounded"
              style={messageBoxStyles}
            >
              {message}
            </div>
          )}

          <div className="grid gap-4">
            <input
              className="input input-bordered w-full focus:outline-none focus:ring-2 focus:ring-[#98A1BC] border-[#98A1BC]"
              placeholder="Name"
              value={name}
              onChange={e => setName(e.target.value)}
            />
            <input
              className="input input-bordered w-full focus:outline-none focus:ring-2 focus:ring-[#98A1BC] border-[#98A1BC]"
              placeholder="City"
              value={city}
              onChange={e => setCity(e.target.value)}
            />
            <input
              className="input input-bordered w-full focus:outline-none focus:ring-2 focus:ring-[#98A1BC] border-[#98A1BC]"
              placeholder="Contact Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
            <button
              className="w-full font-bold text-white btn"
              style={buttonStyles}
              onClick={saveProfile}
            >
              Save Profile
            </button>
            {userId && (
              <p className="text-sm text-[#98A1BC] opacity-80 text-center mt-2 break-all">
                Your unique ID is: {userId}
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
