import React, { useState, useEffect } from 'react';
import { API_URL, authHeader } from '../api';
import { getRole, getUserId } from '../auth';

export default function Profile() {
  const role = getRole();
  const [formData, setFormData] = useState({
    name: '',
    city: '',
    email: '',
    // Additional fields for different roles
    phone: '',
    address: ''
  });
  const [message, setMessage] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [existingId, setExistingId] = useState(null);

  // Check if profile already exists on component mount
  useEffect(() => {
    const id = role === 'donor' ? localStorage.getItem('donorId') : localStorage.getItem('ngoId');
    if (id) {
      setExistingId(id);
      setIsEditing(true);
      setMessage(`You already have a ${role} profile (ID: ${id}). You can update it below.`);
    }
  }, [role]);

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const createOrUpdateProfile = async () => {
    setMessage('');

    // Basic validation
    if (!formData.name || !formData.city || !formData.email) {
      setMessage('Name, City, and Email are required.');
      return;
    }

    const endpoint = role === 'donor' ? 'donors' : 'ngos';
    const method = isEditing ? 'PUT' : 'POST';
    const url = isEditing ? `${API_URL}/${endpoint}/${existingId}` : `${API_URL}/${endpoint}`;

    try {
      // Prepare payload based on role
      const payload = role === 'donor' 
        ? {
            name: formData.name,
            city: formData.city,
            email: formData.email,
            phone: formData.phone || null,
            address: formData.address || null
          }
        : {
            name: formData.name,
            city: formData.city,
            email: formData.email,
            phone: formData.phone || null,
            address: formData.address || null
          };

      const res = await fetch(url, {
        method: method,
        headers: { 
          'Content-Type': 'application/json', 
          ...authHeader() 
        },
        body: JSON.stringify(payload)
      });

      const data = await res.json();

      if (!res.ok) {
        throw new Error(data.detail || `Failed to ${isEditing ? 'update' : 'create'} profile`);
      }

      // Store the ID locally
      if (role === 'donor') {
        localStorage.setItem('donorId', data.id);
      } else {
        localStorage.setItem('ngoId', data.id);
      }

      setExistingId(data.id);
      setIsEditing(true);
      setMessage(`Successfully ${isEditing ? 'updated' : 'created'} ${role} profile! ID: ${data.id}`);

    } catch (error) {
      setMessage(error.message);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#F9F3EF] p-6">
      <div className="w-full max-w-md shadow-xl bg-white rounded-2xl border border-[#98A1BC]/30 p-6">
        <h2 className="text-2xl font-bold text-center text-[#555879] mb-6">
          {isEditing ? 'Update' : 'Create'} {role?.toUpperCase()} Profile
        </h2>

        {message && (
          <div className={`mb-4 p-3 rounded-lg text-sm ${
            message.includes('Successfully') 
              ? 'bg-green-100 border border-green-300 text-green-700' 
              : 'bg-red-100 border border-red-300 text-red-700'
          }`}>
            {message}
          </div>
        )}

        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-[#555879] mb-1">
              Name *
            </label>
            <input
              name="name"
              className="w-full px-4 py-2 rounded-lg border border-[#98A1BC]/50 focus:border-[#555879] focus:ring-2 focus:ring-[#98A1BC]"
              placeholder="Organization or Your Name"
              value={formData.name}
              onChange={handleInputChange}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-[#555879] mb-1">
              Email *
            </label>
            <input
              name="email"
              type="email"
              className="w-full px-4 py-2 rounded-lg border border-[#98A1BC]/50 focus:border-[#555879] focus:ring-2 focus:ring-[#98A1BC]"
              placeholder="contact@example.com"
              value={formData.email}
              onChange={handleInputChange}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-[#555879] mb-1">
              City *
            </label>
            <input
              name="city"
              className="w-full px-4 py-2 rounded-lg border border-[#98A1BC]/50 focus:border-[#555879] focus:ring-2 focus:ring-[#98A1BC]"
              placeholder="e.g., Johannesburg"
              value={formData.city}
              onChange={handleInputChange}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-[#555879] mb-1">
              Phone
            </label>
            <input
              name="phone"
              type="tel"
              className="w-full px-4 py-2 rounded-lg border border-[#98A1BC]/50 focus:border-[#555879] focus:ring-2 focus:ring-[#98A1BC]"
              placeholder="+27 123 456 7890"
              value={formData.phone}
              onChange={handleInputChange}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-[#555879] mb-1">
              Address
            </label>
            <input
              name="address"
              className="w-full px-4 py-2 rounded-lg border border-[#98A1BC]/50 focus:border-[#555879] focus:ring-2 focus:ring-[#98A1BC]"
              placeholder="Street address"
              value={formData.address}
              onChange={handleInputChange}
            />
          </div>

          <button
            className="w-full py-3 rounded-xl bg-[#555879] hover:bg-[#98A1BC] text-white font-semibold transition duration-200"
            onClick={createOrUpdateProfile}
          >
            {isEditing ? 'Update Profile' : 'Create Profile'}
          </button>

          {existingId && (
            <p className="text-sm text-center text-[#555879]">
              Profile ID: <span className="font-mono">{existingId}</span>
            </p>
          )}
        </div>
      </div>
    </div>
  );
}
